mapper.select:
SELECT *
FROM antpool_payhash_hourly_settlements
WHERE account = #{account}
  AND coin = #{coin}
  AND window_start = #{windowStart}
LIMIT 1

SELECT AVG(total_coin)
FROM antpool_payhash_hourly_settlements
WHERE account = #{account}
  AND coin = #{coin}
  AND status = 'SUCCESS'
  AND window_start >= #{start}
  AND window_start < #{end}

SELECT COUNT(1)
FROM antpool_payhash_hourly_settlements
WHERE account = #{account}
  AND coin = #{coin}
  AND status = 'SUCCESS'
  AND window_start >= #{start}
  AND window_start < #{end}

SELECT COUNT(1)
FROM antpool_payhash_hourly_settlements
WHERE 1 = 1

    AND account = #{account}


    AND coin = #{coin}


    AND window_start >= #{startTime}


    AND window_start <= #{endTime}

SELECT *
FROM antpool_payhash_hourly_settlements
WHERE 1 = 1

    AND account = #{account}


    AND coin = #{coin}


    AND window_start >= #{startTime}


    AND window_start <= #{endTime}

ORDER BY window_start DESC
LIMIT #{size} OFFSET #{offset}

mapper.update:
UPDATE antpool_payhash_hourly_settlements
SET status = #{status},
    updated_time = #{updatedTime}
WHERE id = #{id}
