mapper.insert:
INSERT INTO device_remote_commands (
    command_id, user_id, device_id, command_type, status,
    created_at, executed_at, expires_at, error_message
) VALUES (
    #{commandId}, #{userId}, #{deviceId}, #{commandType}, #{status},
    #{createdAt}, #{executedAt}, #{expiresAt}, #{errorMessage}
)

mapper.select:
SELECT * FROM device_remote_commands
WHERE user_id = #{userId}
  AND device_id = #{deviceId}
  AND command_type IN

      #{type}

  AND status = 'PENDING'
  AND expires_at > #{now}
ORDER BY created_at DESC
LIMIT 1

mapper.update:
UPDATE device_remote_commands
SET status = 'EXPIRED'
WHERE device_id = #{deviceId}
  AND command_type = #{type}
  AND status = 'PENDING'

mapper.select:
SELECT * FROM device_remote_commands
WHERE command_id = #{commandId}
  AND user_id = #{userId}
LIMIT 1

mapper.update:
UPDATE device_remote_commands
SET status = #{status},
    executed_at = #{executedAt},
    error_message = #{errorMessage}
WHERE id = #{id}
