mapper.select:
SELECT COALESCE(SUM(amount_xmr), 0)
FROM xmr_wallet_incoming
WHERE user_id = #{userId}

SELECT COALESCE(SUM(amount_xmr), 0)
FROM xmr_wallet_incoming
WHERE subaddress = #{subaddress}
  AND ts IS NOT NULL
  AND ts >= #{start}
  AND ts < #{end}

SELECT * FROM xmr_wallet_incoming
WHERE settled = 0
  AND ts IS NOT NULL
  AND ts >= #{start}
  AND ts < #{end}
ORDER BY ts ASC
LIMIT #{limit}
